/******************************************************************************
*   FUNCTION NAME: oam_son_send_son_oam_rach_opt_ue_info_report
*
*   DESCRIPTION:
*       This function constructs and sends SON_OAM_RACH_OPT_UE_INFO_REPORT message
*
*   RETURNS:
*       OAM_FAILURE     - Indicates failed message processing
*       OAM_SUCCESS     - Indicates successful message processing
*
******************************************************************************/
oam_return_et
oam_son_send_son_oam_rach_opt_ue_info_report
(
    son_oam_rach_opt_ue_info_report_t  *p_son_oam_rach_opt_ue_info_report,           /* Pointer to API specific information. */
    U16                 src_module_id,  /* Source module identifier */
    U16                 dest_module_id,  /* Destination module identifier */
    U16                 transaction_id, /* Interface transaction identifier */
    U8                  cell_index
)
{
    U16 msg_length, msg_api_length;
    U8 *p_msg, *p_oam_msg;

    OAM_NULL_CHECK(p_son_oam_rach_opt_ue_info_report != SON_PNULL);

    SET_CELL_INDEX(cell_index);

    /* Get API length */
    msg_length = son_get_son_oam_rach_opt_ue_info_report_len(p_son_oam_rach_opt_ue_info_report);
   if(msg_length == OAM_FAILURE)
    {
        return OAM_FAILURE;
    }

    OAM_LOG(OAM,OAM_INFO, "Src(%u)->Dst(%u):SON_OAM_RACH_OPT_UE_INFO_REPORT", src_module_id, dest_module_id);

    msg_api_length = msg_length + SON_API_HEADER_SIZE + EXT_MSG_API_HDR_SIZE;

    /* Allocate buffer */
    p_msg = oam_msg_mem_get(msg_api_length);

    if (p_msg == SON_PNULL)
    {
        OAM_LOG(OAM,OAM_ERROR,
                "Memory allocation to p_msg failed."
                " Cannot send message");
        /* Not enough memory */
        return OAM_FAILURE;
    }

    oam_memset(p_msg, 0, msg_api_length);
    p_oam_msg = p_msg;

    /* Fill CSPL header */
    
    oam_construct_api_header(p_oam_msg, SON_VERSION_ID, src_module_id,
        dest_module_id, SON_OAM_RACH_OPT_UE_INFO_REPORT,msg_api_length);
     
    /* Fill interface header */
     p_oam_msg = p_oam_msg + SON_API_HEADER_SIZE;
    oam_construct_interface_api_header(p_oam_msg, transaction_id,  src_module_id,
        dest_module_id, SON_OAM_RACH_OPT_UE_INFO_REPORT, msg_length, cell_index);

    /* Fill SON_OAM_RACH_OPT_UE_INFO_REPORT message */
    p_oam_msg = p_oam_msg + EXT_MSG_API_HDR_SIZE;
    if (OAM_FAILURE == son_compose_son_oam_rach_opt_ue_info_report(&p_oam_msg, p_son_oam_rach_opt_ue_info_report))
    {
        oam_mem_free(p_msg,&oam_error);

        return OAM_FAILURE;
    }

    if((p_oam_msg - msg_api_length) != p_msg)
    {
        oam_mem_free(p_msg,&oam_error);

        return OAM_FAILURE;
    }
    /* Send message to */
    oam_rrm_send_message(p_msg, dest_module_id);

    return OAM_SUCCESS;
}

